var PatternBoolean = "(true|false)",
  PatternNumber = "(0|[1-9][0-9]*)",
  PatternString = "(.*)",
  PatternNever = "(?!.*)",
  PatternBooleanExact = `^${PatternBoolean}$`,
  PatternNumberExact = `^${PatternNumber}$`,
  PatternStringExact = `^${PatternString}$`,
  PatternNeverExact = `^${PatternNever}$`;